import { PrismaClient } from "@prisma/client";
import bcrypt from "bcryptjs";

const prisma = new PrismaClient();

async function cleanDatabase() {
  console.log("üßπ Membersihkan database sebelum seeding...");

  try {
    // Hapus data dengan urutan yang benar (child tables dulu, lalu parent tables)
    await prisma.notificationRead.deleteMany({});
    console.log("   ‚úì Semua data notification reads dihapus");

    await prisma.notification.deleteMany({});
    console.log("   ‚úì Semua data notifications dihapus");

    await prisma.qrScan.deleteMany({});
    console.log("   ‚úì Semua data QR scans dihapus");

    await prisma.workerList.deleteMany({});
    console.log("   ‚úì Semua data worker list dihapus");

    await prisma.submission.deleteMany({});
    console.log("   ‚úì Semua data submission dihapus");

    await prisma.refreshToken.deleteMany({});
    console.log("   ‚úì Semua data refresh tokens dihapus");

    await prisma.session.deleteMany({});
    console.log("   ‚úì Semua data sessions dihapus");

    await prisma.account.deleteMany({});
    console.log("   ‚úì Semua data accounts dihapus");

    await prisma.user.deleteMany({});
    console.log("   ‚úì Semua data user dihapus");

    console.log("üßπ Database berhasil dibersihkan");
  } catch (error) {
    console.error("‚ùå Error saat membersihkan database:", error);
    throw error;
  }
}

