      },
      select: {
        id: true,
        officer_name: true,
        email: true,
        role: true,
        address: true,
        phone_number: true,
        vendor_name: true,
        created_at: true,
        verified_at: true,
        verified_by: true,
      }
    });

    return NextResponse.json(newUser, { status: 201 });

  } catch (error) {
    console.error("Error creating user:", error);
    return NextResponse.json(
      { error: "Terjadi kesalahan saat membuat user" },
      { status: 500 }
    );
  }
}

