      pending: stats.find(s => s.approval_status === 'PENDING')?._count.approval_status || 0,
      approved: stats.find(s => s.approval_status === 'APPROVED')?._count.approval_status || 0,
      rejected: stats.find(s => s.approval_status === 'REJECTED')?._count.approval_status || 0
    };

    return NextResponse.json({
      statistics
    });

  } catch (error) {
    console.error("Vendor statistics error:", error);
    return NextResponse.json(
      { error: "Internal server error" },
      { status: 500 }
    );
  }
}

import { NextResponse } from 'next/server';
import { getServerSession } from 'next-auth';
import { authOptions } from '@/lib/auth';
import { prisma } from '@/lib/prisma';

export async function GET() {
  try {
    const session = await getServerSession(authOptions);

    if (!session?.user?.id) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    if (session.user.role !== 'VERIFIER') {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    // Get today's date range
    const today = new Date();
    const todayStart = new Date(today.getFullYear(), today.getMonth(), today.getDate());
    const todayEnd = new Date(today.getFullYear(), today.getMonth(), today.getDate() + 1);

    // Fetch stats in parallel
    const [
      totalScans,
      todayScans,
      totalSubmissions,
      approvedSubmissions,
      pendingSubmissions,
      rejectedSubmissions
    ] = await Promise.all([
      // Total scans by this verifier
      prisma.qrScan.count({
        where: {
          scanned_by: session.user.id
        }
      }),

      // Today's scans by this verifier
      prisma.qrScan.count({
        where: {
          scanned_by: session.user.id,
          scanned_at: {
            gte: todayStart,
            lt: todayEnd
          }
        }
      }),

      // Total submissions
      prisma.submission.count(),

      // Approved submissions
      prisma.submission.count({
        where: {
          approval_status: 'APPROVED'
        }
      }),

      // Pending submissions
      prisma.submission.count({
        where: {
          approval_status: 'PENDING'
        }
      }),

      // Rejected submissions
      prisma.submission.count({
        where: {
          approval_status: 'REJECTED'
        }
      })
    ]);

    const stats = {
      totalScans,
      todayScans,
      totalSubmissions,
      approvedSubmissions,
      pendingSubmissions,
      rejectedSubmissions
    };

    return NextResponse.json(stats);

  } catch (error) {
    console.error('Error fetching verifier stats:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getServerSession } from 'next-auth';
import { authOptions } from '@/lib/auth';
import { db } from '@/lib/db';

export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession(authOptions);

    if (!session || session.user.role !== 'VERIFIER') {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      );
    }

    const url = new URL(request.url);
    const limit = parseInt(url.searchParams.get('limit') || '20');
    const offset = parseInt(url.searchParams.get('offset') || '0');
    const status = url.searchParams.get('status') || 'ALL';
    const dateRange = url.searchParams.get('dateRange') || 'ALL';
    const search = url.searchParams.get('search') || '';

    // Build where conditions
    const whereConditions: any = {};

    // Status filter
    if (status !== 'ALL') {
      whereConditions.approval_status = status;
    }

    // Date range filter
    if (dateRange !== 'ALL') {
      const now = new Date();
      let startDate: Date;

      switch (dateRange) {
        case 'TODAY':
          startDate = new Date(now);
          startDate.setHours(0, 0, 0, 0);
          whereConditions.created_at = {
            gte: startDate
          };
          break;
        case 'WEEK':
          startDate = new Date(now);
          startDate.setDate(now.getDate() - 7);
          whereConditions.created_at = {
            gte: startDate
          };
          break;
        case 'MONTH':
          startDate = new Date(now);
          startDate.setDate(now.getDate() - 30);
          whereConditions.created_at = {
            gte: startDate
          };
          break;
      }
    }

    // Search filter
    if (search.trim()) {
      whereConditions.OR = [
        {
          simlok_number: {
            contains: search
          }
        },
        {
          vendor_name: {
            contains: search
          }
        },
        {
          officer_name: {
            contains: search
          }
        },
        {
          job_description: {
            contains: search
          }
        },
        {
          work_location: {
            contains: search
          }
        }
      ];
    }

    // Fetch submissions with pagination
    const [submissions, totalCount] = await Promise.all([
      db.submission.findMany({
        where: whereConditions,
        take: limit,
        skip: offset,
        orderBy: [
          {
            approval_status: 'asc' // Show pending first
          },
          {
            created_at: 'desc'
          }
        ],
        select: {
          id: true,
          vendor_name: true,
          officer_name: true,
          job_description: true,
          work_location: true,
          implementation: true,
          approval_status: true,
          simlok_number: true,
          simlok_date: true,
          created_at: true
        }
      }),
      db.submission.count({
        where: whereConditions
      })
    ]);

    return NextResponse.json({
      submissions,
      pagination: {
        total: totalCount,
        limit,
        offset,
        hasMore: offset + limit < totalCount
      },
      filters: {
        status,
        dateRange,
        search
      }
    });

  } catch (error) {
    console.error('Error fetching submissions:', error);
    return NextResponse.json(
      { error: 'Failed to fetch submissions' },
      { status: 500 }
    );
  }
}

