"use client";

import { useSession } from "next-auth/react";
import { useState, useEffect, useCallback } from "react";
import Link from "next/link";
import { 
  ClockIcon,
  PlusIcon,
  CheckCircleIcon,
  XCircleIcon
} from "@heroicons/react/24/outline";
import Card from "../ui/Card";
import { Badge } from "../ui/Badge";
import Button from "../ui/button/Button";
import Alert from "../ui/alert/Alert";
import LoadingSpinner from "../ui/LoadingSpinner";
import ConfirmModal from "../ui/modal/ConfirmModal";
import SubmissionDetailModal from "../vendor/SubmissionDetailModal";
import { useStatsStore } from "@/store/useStatsStore";
import { useSubmissionStore } from "@/store/useSubmissionStore";
import { useSocket } from "@/components/common/RealtimeUpdates";

interface VendorSubmission {
  id: string;
  approval_status: string;
  vendor_name: string;
  based_on: string;
  officer_name: string;
  job_description: string;
  work_location: string;
  implementation: string | null;
  working_hours: string;
  other_notes?: string;
  work_facilities: string;
  simja_number?: string;
  simja_date?: string | null;
  sika_number?: string;
  sika_date?: string | null;
  simlok_number?: string;
  simlok_date?: string | null;
  worker_names: string;
  content: string;
  notes?: string;
  sika_document_upload?: string;
  simja_document_upload?: string;
  qrcode?: string;
  created_at: string;
  signer_position?: string;
  signer_name?: string;
  user: {
    id: string;
    officer_name: string;
    email: string;
    vendor_name: string;
  };
  approvedByUser?: {
    id: string;
    officer_name: string;
    email: string;
  };
}

export default function VendorDashboard() {
  const { data: session } = useSession();

  // Use Zustand stores for realtime data
  const { 
    vendorStats,
    loading: statsLoading,
    fetchVendorStats 
  } = useStatsStore();

  const { 
    submissions,
    loading: submissionsLoading,
    fetchVendorSubmissions 
  } = useSubmissionStore();

  // Initialize Socket.IO connection
  useSocket();

  // Modal state
  const [selectedSubmission, setSelectedSubmission] = useState<VendorSubmission | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Alert state
  const [alert, setAlert] = useState<{
    show: boolean;
    variant: 'success' | 'error' | 'warning' | 'info';
    title: string;
    message: string;
  }>({
    show: false,
    variant: 'info',
    title: '',
    message: ''
  });

  // Confirm modal state
  const [confirmModal, setConfirmModal] = useState<{
    show: boolean;
    title: string;
    message: string;
    onConfirm: () => void;
    isLoading: boolean;
  }>({
    show: false,
    title: '',
    message: '',
    onConfirm: () => {},
    isLoading: false
  });

  useEffect(() => {
    // Initialize data fetching
    if (session?.user?.id) {
      fetchVendorStats();
      fetchVendorSubmissions();
    }
  }, [session?.user?.id, fetchVendorStats, fetchVendorSubmissions]);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('id-ID', {
      day: '2-digit',
      month: 'short',
      year: 'numeric'
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'PENDING': return <Badge variant="warning">Menunggu</Badge>;
      case 'APPROVED': return <Badge variant="success">Disetujui</Badge>;
      case 'REJECTED': return <Badge variant="destructive">Ditolak</Badge>;
      default: return <Badge>{status}</Badge>;
    }
  };

  const handleDelete = useCallback((id: string) => {
    setConfirmModal({
      show: true,
      title: 'Konfirmasi Hapus',
      message: 'Apakah Anda yakin ingin menghapus pengajuan ini? Tindakan ini tidak dapat dibatalkan.',
      onConfirm: () => performDelete(id),
      isLoading: false
    });
  }, []);

  const performDelete = async (id: string) => {
    try {
      setConfirmModal(prev => ({ ...prev, isLoading: true }));

      const response = await fetch(`/api/submissions/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Gagal menghapus pengajuan');
      }

      // Close confirm modal
      setConfirmModal(prev => ({ ...prev, show: false, isLoading: false }));

      // Refresh data setelah berhasil menghapus
      await fetchVendorSubmissions();
      await fetchVendorStats();

      // Show success message
      setAlert({
        show: true,
        variant: 'success',
        title: 'Berhasil!',
        message: 'Pengajuan berhasil dihapus'
      });

      // Auto hide after 3 seconds
      setTimeout(() => setAlert(prev => ({ ...prev, show: false })), 3000);
    } catch (error) {
      setConfirmModal(prev => ({ ...prev, isLoading: false }));

      console.error('Error deleting submission:', error);
      setAlert({
        show: true,
        variant: 'error',
        title: 'Gagal Menghapus',
        message: error instanceof Error ? error.message : 'Gagal menghapus pengajuan'
      });

      // Auto hide after 5 seconds
      setTimeout(() => setAlert(prev => ({ ...prev, show: false })), 5000);
    }
  };

  const handleViewDetail = useCallback(async (submission: VendorSubmission) => {
    try {
      // Fetch full submission data from API
      const response = await fetch(`/api/submissions/${submission.id}`);
      if (response.ok) {
        const fullSubmissionData = await response.json();
        setSelectedSubmission(fullSubmissionData);
        setIsModalOpen(true);
      } else {
        console.error('Failed to fetch submission details');
        setAlert({
          show: true,
          variant: 'error',
          title: 'Error',
          message: 'Gagal memuat detail pengajuan'
        });
      }
    } catch (error) {
      console.error('Error fetching submission details:', error);
      setAlert({
        show: true,
        variant: 'error',
        title: 'Error',
        message: 'Terjadi kesalahan saat memuat detail pengajuan'
      });
    }
  }, []);

  const handleCloseModal = useCallback(() => {
    setIsModalOpen(false);
    setSelectedSubmission(null);
  }, []);

  return (
    <div className="space-y-8">
      {/* Alert Notification */}
      {alert.show && (
        <div className="fixed top-4 right-4 z-50 max-w-md">
          <Alert
            variant={alert.variant}
            title={alert.title}
            message={alert.message}
          />
        </div>
      )}

      {/* Header */}
      <div className="bg-white rounded-xl border border-gray-200 p-6">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
          <div>
            <h1 className="text-2xl font-semibold text-gray-900">
