'use client';

import React, { useState, useEffect } from 'react';
import {
  MagnifyingGlassIcon,
  FunnelIcon,
  QrCodeIcon,
  UserIcon,
  DocumentTextIcon,
  ClockIcon,
  EyeIcon,
  ChevronLeftIcon,
  ChevronRightIcon
} from '@heroicons/react/24/outline';
import { formatDistanceToNow, format } from 'date-fns';
import { id } from 'date-fns/locale';
import Button from '@/components/ui/button/Button';
import { Badge } from '@/components/ui/Badge';
import Alert from '@/components/ui/alert/Alert';
import DatePicker from '@/components/form/DatePicker';
import SimlokPdfModal from '@/components/common/SimlokPdfModal';

interface QrScan {
  id: string;
  submission_id: string;
  scanned_by: string;
  scanned_at: string;
  scanner_name?: string;
  scan_location?: string;
  notes?: string;
  submission: {
    id: string;
    simlok_number: string;
    vendor_name: string;
    job_description: string;
    review_status: string;
    final_status: string;
  };
  user: {
    id: string;
    full_name: string;
    email: string;
    role: string;
  };
}

interface FilterState {
  dateFrom: string;
  dateTo: string;
  verifier: string;
  submissionId: string;
}

export default function ReviewerScanHistoryContent() {
  const [scans, setScans] = useState<QrScan[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string>('');
  const [filters, setFilters] = useState<FilterState>({
    dateFrom: '',
    dateTo: '',
    verifier: '',
    submissionId: ''
  });
  const [showFilters, setShowFilters] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [selectedScan, setSelectedScan] = useState<QrScan | null>(null);
  const [showPdfModal, setShowPdfModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const pageSize = 15;

  const fetchScanHistory = async () => {
    try {
      setLoading(true);
      setError('');

      const params = new URLSearchParams({
        page: currentPage.toString(),
        limit: pageSize.toString(),
        ...(filters.dateFrom && { dateFrom: filters.dateFrom }),
        ...(filters.dateTo && { dateTo: filters.dateTo }),
        ...(filters.verifier && { verifier: filters.verifier }),
        ...(filters.submissionId && { submissionId: filters.submissionId }),
        ...(searchTerm && { search: searchTerm })
      });

      const response = await fetch(`/api/scan-history?${params}`);

      if (!response.ok) {
        throw new Error('Failed to fetch scan history');
      }

      const data = await response.json();
      setScans(data.scans || []);
      setTotalPages(Math.ceil((data.total || 0) / pageSize));
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      fetchScanHistory();
    }, 300);

    return () => clearTimeout(timer);
  }, [currentPage, searchTerm]);

  const handleFilterSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setCurrentPage(1);
    fetchScanHistory();
  };

  const resetFilters = () => {
    setFilters({
      dateFrom: '',
      dateTo: '',
      verifier: '',
      submissionId: ''
    });
    setSearchTerm('');
    setCurrentPage(1);
    fetchScanHistory();
  };

  const getStatusBadge = (status: string, type: 'review' | 'final') => {
    if (type === 'review') {
      switch (status) {
        case 'PENDING_REVIEW':
          return <Badge variant="warning">Menunggu Review</Badge>;
        case 'MEETS_REQUIREMENTS':
          return <Badge variant="success">Memenuhi Syarat</Badge>;
        case 'NOT_MEETS_REQUIREMENTS':
          return <Badge variant="destructive">Tidak Memenuhi Syarat</Badge>;
        default:
          return null;
      }
    } else {
      switch (status) {
        case 'PENDING_APPROVAL':
          return <Badge variant="warning">Menunggu Persetujuan</Badge>;
        case 'APPROVED':
          return <Badge variant="success">Disetujui</Badge>;
        case 'REJECTED':
          return <Badge variant="destructive">Ditolak</Badge>;
        default:
          return null;
      }
    }
  };

  return (
    <div className="space-y-6">
      {/* Header with Search and Filters */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4 mb-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-blue-100 rounded-lg">
              <QrCodeIcon className="h-6 w-6 text-blue-600" />
            </div>
            <div>
              <h2 className="text-xl font-semibold text-gray-900">Manajemen Riwayat Scan</h2>
              <p className="text-sm text-gray-500">Pantau aktivitas scan QR code oleh verifikator</p>
            </div>
          </div>

          <div className="flex items-center space-x-3">
            <Button
              onClick={() => setShowFilters(!showFilters)}
              variant={showFilters ? 'secondary' : 'primary'}
              className="flex items-center space-x-2"
            >
              <FunnelIcon className="h-4 w-4" />
              <span>Filter</span>
            </Button>
          </div>
        </div>

        {/* Search Bar */}
        <div className="relative mb-4">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Cari berdasarkan nomor simlok, nama perusahaan, atau verifikator..."
