import { X, CheckCircle, User, Calendar, MapPin, QrCode } from 'lucide-react';

interface Submission {
  id: string;
  number: string;
  title: string;
  task: string;
  workers?: { name: string }[];
  vendor?: { name: string };
  location?: string;
  implementation_start_date?: string | null;
  implementation_end_date?: string | null;
  status: string;
}

interface ScanResult {
  success: boolean;
  data?: any;
  error?: string;
  message?: string;
}

interface QrScanResultModalProps {
  isOpen: boolean;
  onClose: () => void;
  result: ScanResult;
  submission?: Submission | null;
  scanTime?: Date;
  scannedBy?: string;
  onScanAnother: () => void;
}

const QrScanResultModal: React.FC<QrScanResultModalProps> = ({
  isOpen,
  onClose,
  result,
  submission,
  scanTime,
  scannedBy,
  onScanAnother
}) => {
  // Extract submission from result if not provided directly
  const submissionData = submission || result.data?.submission;

  if (!isOpen || !result.success || !submissionData) return null;

  const formatDate = (dateString: string | null) => {
    if (!dateString) return 'Tidak ditentukan';
    return new Date(dateString).toLocaleDateString('id-ID', {
      day: 'numeric',
      month: 'long',
      year: 'numeric'
    });
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'completed':
        return 'text-green-600 bg-green-100';
      case 'in_progress':
        return 'text-blue-600 bg-blue-100';
      case 'pending':
        return 'text-yellow-600 bg-yellow-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusText = (status: string) => {
    switch (status.toLowerCase()) {
      case 'completed':
        return 'Selesai';
      case 'in_progress':
        return 'Sedang Berlangsung';
      case 'pending':
        return 'Menunggu';
      default:
        return status;
    }
  };

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-green-50">
          <div className="flex items-center space-x-2">
            <CheckCircle className="h-6 w-6 text-green-600" />
