    emitToReviewer(EVENT_NAMES.NOTIFICATION_UNREAD_COUNT, payload);
  } else if (scope === 'approver') {
    emitToApprover(EVENT_NAMES.NOTIFICATION_UNREAD_COUNT, payload);
  } else if (scope === 'vendor' && vendorId) {
