import { PrismaClient } from "@prisma/client";

const prisma = new PrismaClient();

async function checkSubmissionStatus() {
  console.log("ğŸ” Mengecek status semua submission...\n");

  const submissions = await prisma.submission.findMany({
    include: {
      user: {
        select: {
          vendor_name: true,
          officer_name: true,
        }
      }
    },
    orderBy: {
      created_at: 'desc'
    }
  });

  console.log(`ğŸ“Š Total submissions: ${submissions.length}\n`);

  // Group by status
  const statusCount = submissions.reduce((acc, sub) => {
    acc[sub.approval_status] = (acc[sub.approval_status] || 0) + 1;
    return acc;
  }, {} as Record<string, number>);

  console.log("ğŸ“ˆ Status breakdown:");
  Object.entries(statusCount).forEach(([status, count]) => {
    const emoji = status === 'PENDING' ? 'â³' : status === 'APPROVED' ? 'âœ…' : 'âŒ';
    console.log(`   ${emoji} ${status}: ${count} submissions`);
  });

  console.log("\nğŸ“‹ Detail submissions:");
  submissions.forEach((sub, index) => {
    const statusEmoji = sub.approval_status === 'PENDING' ? 'â³' : sub.approval_status === 'APPROVED' ? 'âœ…' : 'âŒ';
    console.log(`   ${index + 1}. ${statusEmoji} ${sub.user.vendor_name} - ${sub.job_description}`);
    console.log(`      Status: ${sub.approval_status}`);
    console.log(`      Created: ${sub.created_at.toLocaleDateString('id-ID')}`);
    console.log(`      QR Code: ${sub.qrcode}`);
    console.log("");
  });

  // Check specific fields for pending submissions
  const pendingSubmissions = submissions.filter(sub => sub.approval_status === 'PENDING');

  if (pendingSubmissions.length > 0) {
    console.log("ğŸ” Verifikasi submission pending:");
    console.log(`   âœ“ Semua ${pendingSubmissions.length} submission berstatus PENDING`);
    console.log("   âœ“ Tidak ada approved_by yang terisi");
    console.log("   âœ“ Tidak ada simlok_number yang terisi");
    console.log("   âœ“ Tidak ada implementation_start_date yang terisi");
    console.log("   âœ“ Tidak ada implementation_end_date yang terisi");

    // Verify no approved fields are filled
    const hasApprovedFields = pendingSubmissions.some(sub => 
      sub.approved_by || 
      sub.simlok_number || 
      sub.implementation_start_date || 
      sub.implementation_end_date ||
      sub.implementation ||
      sub.other_notes ||
      sub.content
    );

    if (hasApprovedFields) {
      console.log("   âš ï¸ Warning: Beberapa submission pending memiliki data approval!");
    } else {
      console.log("   âœ… Semua submission pending tidak memiliki data approval");
    }
  }
}

checkSubmissionStatus()
  .catch((e) => {
    console.error("âŒ Error:", e);
    process.exit(1);
  })
  .finally(async () => {
    await prisma.$disconnect();
  });

const { PrismaClient } = require('@prisma/client');
const prisma = new PrismaClient();

async function cleanupOrphanedNotifications() {
  try {
    console.log('ğŸ§¹ Starting cleanup of orphaned notifications...');

    // Find all notifications that reference submissions
    const submissionNotifications = await prisma.notification.findMany({
      where: {
        OR: [
          { data: { contains: 'submissionId' } },
          { data: { contains: 'submission_id' } },
          { message: { contains: 'ID' } },
          { title: { contains: 'ID' } }
        ]
      }
    });

    console.log(`Found ${submissionNotifications.length} notifications that might reference submissions`);

    const orphanedNotifications = [];

    // Check each notification
    for (const notif of submissionNotifications) {
      let submissionId = null;

      // Extract submission ID from data field
      if (notif.data) {
        try {
          const data = typeof notif.data === 'string' ? JSON.parse(notif.data) : notif.data;
          submissionId = data.submissionId || data.submission_id;
        } catch (e) {
          // ignore parsing errors
        }
      }

      // Extract from message/title if not found in data
      if (!submissionId) {
        const idPattern = /ID[:\s]+([a-zA-Z0-9]+)/i;
        const messageMatch = notif.message.match(idPattern);
        const titleMatch = notif.title.match(idPattern);
        submissionId = messageMatch?.[1] || titleMatch?.[1];
      }

      if (submissionId) {
        // Check if submission exists
        const submission = await prisma.submission.findUnique({
          where: { id: submissionId }
        });

        if (!submission) {
          orphanedNotifications.push({
            notification: notif,
            submissionId: submissionId
          });
        }
      }
    }

    console.log(`\nFound ${orphanedNotifications.length} orphaned notifications:`);

    if (orphanedNotifications.length > 0) {
      console.log('\nğŸ“‹ Orphaned notifications:');
      orphanedNotifications.forEach((item, index) => {
        console.log(`${index + 1}. ${item.notification.type} - ${item.notification.title}`);
        console.log(`   Missing submission ID: ${item.submissionId}`);
        console.log(`   Created: ${item.notification.created_at}`);
      });

      console.log('\nâ“ Cleaning up orphaned notifications automatically...');

      // Delete orphaned notifications
      console.log('\nğŸ—‘ï¸ Deleting orphaned notifications...');

      for (const item of orphanedNotifications) {
        // First delete NotificationRead records
        await prisma.notificationRead.deleteMany({
          where: { notification_id: item.notification.id }
        });

        // Then delete the notification
        await prisma.notification.delete({
          where: { id: item.notification.id }
        });

        console.log(`âœ… Deleted notification ${item.notification.id} (${item.notification.type})`);
      }

      console.log(`\nâœ… Successfully cleaned up ${orphanedNotifications.length} orphaned notifications`);
    } else {
      console.log('âœ… No orphaned notifications found!');
    }

  } catch (error) {
    console.error('âŒ Error during cleanup:', error);
  } finally {
    await prisma.$disconnect();
  }
}

cleanupOrphanedNotifications();

