  useEffect(() => {
    if (status === 'unauthenticated' && autoRedirect && countdown > 0) {
      const timer = setTimeout(() => {
        setCountdown(countdown - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (status === 'unauthenticated' && autoRedirect && countdown === 0) {
      router.push("/login");
