    </label>
  );
}

'use client';

import { useState, useEffect, useCallback, forwardRef } from 'react';
import { CalendarIcon } from '@heroicons/react/24/outline';

interface DatePickerProps {
  id?: string;
  name?: string;
  value?: string;
  onChange?: (value: string) => void;
  placeholder?: string;
  required?: boolean;
  disabled?: boolean;
  showTimeSelect?: boolean;
  timeIntervals?: number;
  dateFormat?: string;
  className?: string;
}

// Safe React DatePicker component with fallback
function SafeReactDatePicker({
  id,
  name,
  value,
  onChange,
  placeholder = "Pilih tanggal",
  required = false,
  disabled = false,
  className
}: DatePickerProps) {
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [hasError, setHasError] = useState(false);

  // Update selectedDate when value prop changes
  useEffect(() => {
    try {
      if (value) {
        const [year, month, day] = value.split('-').map(Number);
        if (year && month && day && !isNaN(year) && !isNaN(month) && !isNaN(day)) {
          setSelectedDate(new Date(year, month - 1, day));
        }
      } else {
        setSelectedDate(null);
      }
    } catch (error) {
      console.warn('DatePicker: Error parsing date value:', error);
      setHasError(true);
    }
  }, [value]);

  const handleDateChange = useCallback((date: Date | null) => {
    try {
      setSelectedDate(date);
      if (onChange) {
        if (date) {
          const year = date.getFullYear();
          const month = String(date.getMonth() + 1).padStart(2, '0');
          const day = String(date.getDate()).padStart(2, '0');
          const localDateString = `${year}-${month}-${day}`;
          onChange(localDateString);
        } else {
          onChange('');
        }
      }
    } catch (error) {
      console.warn('DatePicker: Error handling date change:', error);
      setHasError(true);
    }
  }, [onChange]);

  // Custom input component
  const CustomInput = forwardRef<HTMLInputElement, any>(({ value, onClick, placeholder, disabled, className }, ref) => (
    <div className="relative">
      <input
        ref={ref}
        value={value}
        onClick={onClick}
        placeholder={placeholder}
        readOnly
        disabled={disabled}
        className={`w-full px-3 py-2 pr-10 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
          disabled ? 'bg-gray-50 cursor-not-allowed' : 'cursor-pointer'
        } ${className || ''}`}
      />
      <CalendarIcon className="absolute right-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400 pointer-events-none" />
    </div>
  ));

  CustomInput.displayName = 'CustomInput';

  // Use native input if React DatePicker fails
  if (hasError) {
    return (
      <NativeDateInput
        id={id}
        name={name}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        required={required}
        disabled={disabled}
        className={className}
      />
    );
  }

  try {
    const ReactDatePicker = require('react-datepicker');
    require('react-datepicker/dist/react-datepicker.css');

    return (
      <ReactDatePicker.default
        id={id}
        name={name}
        selected={selectedDate}
        onChange={handleDateChange}
        dateFormat="dd/MM/yyyy"
        placeholderText={placeholder}
        disabled={disabled}
        required={required}
        customInput={<CustomInput className={className} />}
        popperClassName="react-datepicker-popper"
        calendarClassName="react-datepicker-calendar"
        showPopperArrow={false}
        popperPlacement="bottom-start"
        withPortal={false}
      />
    );
  } catch (error) {
    console.warn('DatePicker: Failed to load react-datepicker, using native input:', error);
    return (
      <NativeDateInput
        id={id}
        name={name}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        required={required}
        disabled={disabled}
        className={className}
      />
    );
  }
}

// Native HTML date input fallback
function NativeDateInput({
  id,
  name,
  value,
  onChange,
  placeholder = "Pilih tanggal",
  required = false,
  disabled = false,
  className
}: DatePickerProps) {
  const [inputValue, setInputValue] = useState<string>('');

  // Update input value when prop changes
  useEffect(() => {
    if (value) {
      setInputValue(value);
    } else {
      setInputValue('');
    }
  }, [value]);

  const handleInputChange = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = event.target.value;
    setInputValue(newValue);

    if (onChange) {
      onChange(newValue);
    }
  }, [onChange]);

  return (
    <div className="relative">
      <input
        id={id}
        name={name}
        type="date"
        value={inputValue}
        onChange={handleInputChange}
        placeholder={placeholder}
        required={required}
        disabled={disabled}
        className={`w-full px-3 py-2 pr-10 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors ${
          disabled ? 'bg-gray-50 cursor-not-allowed' : 'cursor-pointer'
        } ${className || ''}`}
      />
      <CalendarIcon className="absolute right-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400 pointer-events-none" />
    </div>
  );
}

// Main DatePicker component with feature detection
export default function DatePicker(props: DatePickerProps) {
  // Prefer react-datepicker if available and stable, otherwise use native input
  const useReactDatePicker = typeof window !== 'undefined' && 
                           !navigator.userAgent.includes('TestRunner');

  if (useReactDatePicker) {
    return <SafeReactDatePicker {...props} />;
  }

  return <NativeDateInput {...props} />;
}

'use client';

import { useState, useRef } from 'react';
import { DocumentIcon, XMarkIcon, CloudArrowUpIcon } from '@heroicons/react/24/outline';

interface FileUploadProps {
  id?: string;
  name?: string;
  value?: string;
  onChange?: (url: string) => void;
  onFileChange?: (file: File | null) => void;
  accept?: string;
  multiple?: boolean;
  maxSize?: number; // in MB - default is now 8MB
  required?: boolean;
  disabled?: boolean;
  className?: string;
  label?: string;
  description?: string;
}

export default function FileUpload({
  id,
  name,
  value,
  onChange,
  onFileChange,
  accept = ".pdf,.doc,.docx,.jpg,.jpeg,.png",
  multiple = false,
  maxSize = 8, // 8MB default
  required = false,
  disabled = false,
  className,
  label,
  description
}: FileUploadProps) {
  const [dragActive, setDragActive] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [error, setError] = useState<string | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const validateFileInput = (file: File): string | null => {
    // Check file size first
    if (file.size > maxSize * 1024 * 1024) {
      return `Ukuran file terlalu besar. Maksimal ${maxSize}MB`;
    }

    // Check file type based on accept prop
    const acceptedTypes = accept.split(',').map(type => type.trim());
    const fileExtension = '.' + file.name.split('.').pop()?.toLowerCase();

    const isValidType = acceptedTypes.some(type => {
      if (type.startsWith('.')) {
        return type === fileExtension;
      }
      return file.type.match(type);
    });

    if (!isValidType) {
      return `Tipe file tidak didukung. Tipe yang diizinkan: ${accept}`;
    }

    return null;
  };

  const uploadFile = async (file: File): Promise<string> => {
    const formData = new FormData();
    formData.append('file', file);

    // Add field name to help categorize the file
    if (name) {
      formData.append('fieldName', name);
    }

    const response = await fetch('/api/upload', {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.error || 'Upload failed');
    }

    const data = await response.json();
    return data.url;
  };

  const handleFileSelect = async (files: FileList | null) => {
    if (!files || files.length === 0) return;

    const file = files[0];
    if (!file) return;

    setError(null);

    // Validate file
    const validationError = validateFileInput(file);
    if (validationError) {
      setError(validationError);
      return;
    }

    try {
      setUploading(true);
      setUploadProgress(0);

      // Simulate progress for better UX
      const progressInterval = setInterval(() => {
        setUploadProgress(prev => Math.min(prev + 10, 90));
      }, 100);

      const fileUrl = await uploadFile(file);

      clearInterval(progressInterval);
      setUploadProgress(100);

      // Notify parent components
      if (onChange) {
        onChange(fileUrl);
      }
      if (onFileChange) {
        onFileChange(file);
      }

      setTimeout(() => {
        setUploading(false);
        setUploadProgress(0);
      }, 500);

    } catch (error) {
      setError(error instanceof Error ? error.message : 'Upload failed');
      setUploading(false);
      setUploadProgress(0);
    }
  };

  const handleDragEnter = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (disabled) return;

    const files = e.dataTransfer.files;
    handleFileSelect(files);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    handleFileSelect(e.target.files);
  };

  const handleClick = () => {
    if (disabled) return;
    fileInputRef.current?.click();
  };

  const removeFile = () => {
    if (onChange) {
      onChange('');
    }
    if (onFileChange) {
      onFileChange(null);
    }
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
    setError(null);
  };

  const getFileName = () => {
    if (value) {
      return value.split('/').pop() || 'Uploaded file';
    }
    return null;
  };

  return (
    <div className={`space-y-2 ${className || ''}`}>
      {label && (
        <label className="block text-sm font-medium text-gray-700">
          {label}
          {required && <span className="text-red-500 ml-1">*</span>}
        </label>
      )}

      {description && (
        <p className="text-sm text-gray-500">{description}</p>
      )}

      <div
        className={`relative border-2 border-dashed rounded-lg p-6 transition-colors ${
          dragActive 
            ? 'border-blue-400 bg-blue-50' 
            : disabled 
            ? 'border-gray-200 bg-gray-50' 
            : 'border-gray-300 hover:border-gray-400'
        } ${
          !disabled ? 'cursor-pointer' : 'cursor-not-allowed'
        }`}
        onDragEnter={handleDragEnter}
        onDragLeave={handleDragLeave}
        onDragOver={handleDragOver}
        onDrop={handleDrop}
        onClick={handleClick}
      >
        <input
          ref={fileInputRef}
          id={id}
          name={name}
          type="file"
          accept={accept}
          multiple={multiple}
          required={required}
          disabled={disabled}
          onChange={handleInputChange}
          className="hidden"
        />

        {uploading ? (
          <div className="text-center">
            <CloudArrowUpIcon className="mx-auto h-12 w-12 text-blue-500 animate-pulse" />
            <div className="mt-4">
              <div className="text-sm font-medium text-gray-900">Uploading...</div>
              <div className="mt-2 w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${uploadProgress}%` }}
                />
              </div>
              <div className="text-xs text-gray-500 mt-1">{uploadProgress}%</div>
            </div>
          </div>
        ) : value ? (
          <div className="text-center">
            <DocumentIcon className="mx-auto h-12 w-12 text-green-500" />
            <div className="mt-4">
              <div className="text-sm font-medium text-gray-900">{getFileName()}</div>
              <div className="text-xs text-gray-500 mt-1">File uploaded successfully</div>
              <button
                type="button"
                onClick={(e) => {
                  e.stopPropagation();
                  removeFile();
                }}
                className="mt-2 inline-flex items-center px-3 py-1 text-xs font-medium text-red-600 bg-red-50 rounded-md hover:bg-red-100"
              >
                <XMarkIcon className="h-4 w-4 mr-1" />
                Remove
              </button>
            </div>
          </div>
        ) : (
          <div className="text-center">
            <CloudArrowUpIcon className="mx-auto h-12 w-12 text-gray-400" />
            <div className="mt-4">
              <div className="text-sm font-medium text-gray-900">
                {dragActive ? 'Drop file here' : 'Click to upload or drag and drop'}
              </div>
              <div className="text-xs text-gray-500 mt-1">
                {accept} up to {maxSize}MB
              </div>
            </div>
          </div>
        )}
      </div>

      {error && (
        <div className="text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-2">
          {error}
        </div>
      )}
    </div>
  );
}

