              </table>
            </div>
          )}
        </div>
      </div>

      {/* Modal */}
      {selectedSubmission && (
        <ReviewerSubmissionDetailModal
          isOpen={isModalOpen}
          onClose={handleCloseModal}
          submissionId={selectedSubmission}
          onReviewSubmitted={handleReviewSubmitted}
        />
      )}
    </div>
  );
};

