          <div className="flex gap-3 shrink-0 pt-4 md:pt-6 border-t border-gray-200 md:px-2">
            <Button 
              onClick={onClose}
              variant={result.success ? "primary" : "outline"}
              className="flex-1 md:py-3 md:text-base md:font-medium"
            >
              {result.success ? 'Selesai' : 'Tutup'}
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

