import { useEffect, useState } from 'react';
import { XMarkIcon, CheckCircleIcon, ExclamationCircleIcon, InformationCircleIcon, ExclamationTriangleIcon } from '@heroicons/react/24/outline';

export interface ToastProps {
  id: string;
  variant: 'success' | 'error' | 'warning' | 'info';
  title: string;
  message: string;
  duration?: number;
  onClose: (id: string) => void;
}

export default function Toast({ 
  id, 
  variant, 
  title, 
  message, 
  duration = 5000, 
  onClose 
}: ToastProps) {
  const [isVisible, setIsVisible] = useState(false);
  const [isExiting, setIsExiting] = useState(false);

  useEffect(() => {
    // Show animation
    setIsVisible(true);

    // Auto-hide timer
    const timer = setTimeout(() => {
      handleClose();
    }, duration);

    return () => clearTimeout(timer);
  }, [duration]);

  const handleClose = () => {
    setIsExiting(true);
    setTimeout(() => {
      onClose(id);
    }, 300); // Wait for exit animation
  };

  const variantConfig = {
    success: {
      bgColor: 'bg-emerald-50 border-emerald-200',
      iconColor: 'text-emerald-500',
      titleColor: 'text-emerald-800',
      messageColor: 'text-emerald-700',
      icon: CheckCircleIcon
    },
    error: {
      bgColor: 'bg-red-50 border-red-200',
      iconColor: 'text-red-500',
      titleColor: 'text-red-800',
      messageColor: 'text-red-700',
      icon: ExclamationCircleIcon
    },
    warning: {
      bgColor: 'bg-amber-50 border-amber-200',
      iconColor: 'text-amber-500',
      titleColor: 'text-amber-800',
      messageColor: 'text-amber-700',
      icon: ExclamationTriangleIcon
    },
    info: {
      bgColor: 'bg-blue-50 border-blue-200',
      iconColor: 'text-blue-500',
      titleColor: 'text-blue-800',
      messageColor: 'text-blue-700',
      icon: InformationCircleIcon
    }
  };

  const config = variantConfig[variant];
  const IconComponent = config.icon;

  return (
    <div
      className={`
        pointer-events-auto w-full overflow-hidden rounded-lg border shadow-lg
        ${config.bgColor}
        transform transition-all duration-300 ease-in-out
        ${isVisible && !isExiting 
          ? 'translate-x-0 opacity-100' 
          : 'translate-x-full opacity-0'
        }
      `}
      style={{ minWidth: '320px', maxWidth: '400px' }}
    >
      <div className="p-4">
        <div className="flex items-start">
          <div className="flex-shrink-0">
            <IconComponent className={`h-6 w-6 ${config.iconColor}`} />
          </div>
          <div className="ml-3 flex-1 min-w-0">
            <p className={`text-sm font-medium ${config.titleColor} break-words`}>
              {title}
            </p>
            <p className={`mt-1 text-sm ${config.messageColor} break-words`}>
              {message}
            </p>
          </div>
          <div className="ml-4 flex flex-shrink-0">
            <button
              type="button"
              className={`
                inline-flex rounded-md p-1.5 transition-colors duration-200
                ${config.iconColor} hover:bg-black/5 focus:outline-none focus:ring-2 focus:ring-offset-2
              `}
              onClick={handleClose}
            >
              <span className="sr-only">Dismiss</span>
              <XMarkIcon className="h-5 w-5" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useCallback } from 'react';
import Toast from './Toast';

interface ToastData {
  id: string;
  variant: 'success' | 'error' | 'warning' | 'info';
  title: string;
  message: string;
  duration?: number;
}

export interface ToastContextType {
  addToast: (toast: Omit<ToastData, 'id'>) => void;
  removeToast: (id: string) => void;
}

export default function ToastContainer() {
  const [toasts, setToasts] = useState<ToastData[]>([]);

  const removeToast = useCallback((id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  }, []);

  const addToast = useCallback((toast: Omit<ToastData, 'id'>) => {
    const id = Date.now().toString() + Math.random().toString(36).substr(2, 9);
    setToasts(prev => [...prev, { ...toast, id }]);
  }, []);

  // Expose methods globally for easy access
  if (typeof window !== 'undefined') {
    (window as any).showToast = addToast;
  }

  return (
    <div
      className="fixed top-4 right-4 z-[9999] flex flex-col space-y-2 pointer-events-none max-w-sm w-full"
      style={{ zIndex: 9999 }}
    >
      {toasts.map((toast) => (
        <Toast
          key={toast.id}
          id={toast.id}
          variant={toast.variant}
          title={toast.title}
          message={toast.message}
          duration={toast.duration || 5000}
          onClose={removeToast}
        />
      ))}
    </div>
  );
}

"use client";
import React, { useState } from "react";
import { useModal } from "@/hooks/useModal";
import { Modal } from "@/components/ui/modal";
import Button from "@/components/ui/button/Button";
import Input from "@/components/form/input/InputField";
import Label from "@/components/form/Label";

export default function ChangePasswordCard() {
  const { isOpen, openModal, closeModal } = useModal();
  const [passwords, setPasswords] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [errors, setErrors] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setPasswords((prev) => ({
      ...prev,
      [name]: value,
    }));
    // Clear error when user starts typing
    setErrors((prev) => ({
      ...prev,
      [name]: "",
    }));
  };

  const validatePasswords = () => {
    let isValid = true;
    const newErrors = {
      currentPassword: "",
      newPassword: "",
      confirmPassword: "",
    };

    if (!passwords.currentPassword) {
      newErrors.currentPassword = "Password saat ini wajib diisi";
      isValid = false;
    }

    if (!passwords.newPassword) {
      newErrors.newPassword = "Password baru wajib diisi";
      isValid = false;
    } else if (passwords.newPassword.length < 8) {
      newErrors.newPassword = "Password minimal 8 karakter";
      isValid = false;
    }

    if (!passwords.confirmPassword) {
      newErrors.confirmPassword = "Silakan konfirmasi password baru Anda";
      isValid = false;
    } else if (passwords.newPassword !== passwords.confirmPassword) {
      newErrors.confirmPassword = "Password tidak cocok";
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSave = async () => {
    if (validatePasswords()) {
      try {
        const response = await fetch("/api/user/change-password", {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            currentPassword: passwords.currentPassword,
            newPassword: passwords.newPassword,
          }),
        });

        if (!response.ok) {
          const error = await response.text();
          throw new Error(error);
        }

        // Reset form and close modal on success
        closeModal();
        setPasswords({
          currentPassword: "",
          newPassword: "",
          confirmPassword: "",
        });

        // Show success message
        alert("Password berhasil diubah");
      } catch (error) {
        console.error("Error changing password:", error);
        setErrors((prev) => ({
          ...prev,
          currentPassword: error instanceof Error ? error.message : "Gagal mengubah password",
        }));
      }
    }
  };

  return (
    <div className="p-5 border border-gray-200 rounded-2xl lg:p-6">
      <div className="flex flex-col gap-6 lg:flex-row lg:items-start lg:justify-between">
        <div>
          <h4 className="text-lg font-semibold text-gray-800 lg:mb-2">
            Pengaturan Password
          </h4>
          <p className="text-sm text-gray-500">
            Ubah password Anda untuk menjaga keamanan akun
          </p>
        </div>

        <button
          onClick={openModal}
          className="flex w-full items-center justify-center gap-2 rounded-full border border-gray-300 bg-white px-4 py-3 text-sm font-medium text-gray-700 shadow-xs hover:bg-gray-50 hover:text-gray-800 lg:inline-flex lg:w-auto"
        >
          Ubah Password
        </button>
      </div>

      <Modal isOpen={isOpen} onClose={closeModal} className="max-w-[500px] m-4">
        <div className="no-scrollbar relative w-full overflow-y-auto rounded-3xl bg-white p-4 lg:p-8">
          <div className="pr-14">
            <h4 className="mb-2 text-2xl font-semibold text-gray-800">
              Ubah Password
            </h4>
            <p className="mb-6 text-sm text-gray-500">
              Silakan masukkan password saat ini dan pilih password baru.
            </p>
          </div>
          <form className="flex flex-col gap-5">
            <div>
              <Label>Password Saat Ini</Label>
              <Input
                type="password"
                name="currentPassword"
                value={passwords.currentPassword}
                onChange={handleChange}
                error={errors.currentPassword}
              />
            </div>

            <div>
              <Label>Password Baru</Label>
              <Input
                type="password"
                name="newPassword"
                value={passwords.newPassword}
                onChange={handleChange}
                error={errors.newPassword}
              />
            </div>

            <div>
              <Label>Konfirmasi Password Baru</Label>
              <Input
                type="password"
                name="confirmPassword"
                value={passwords.confirmPassword}
                onChange={handleChange}
                error={errors.confirmPassword}
              />
            </div>

            <div className="flex items-center gap-3 mt-6 lg:justify-end">
              <Button size="sm" variant="outline" onClick={closeModal}>
                Batal
              </Button>
              <Button size="sm" onClick={handleSave}>
                Perbarui Password
              </Button>
            </div>
          </form>
        </div>
      </Modal>
    </div>
  );
}

"use client";
